package at.aoc.day6;

import java.io.IOException;
import java.util.List;
import java.util.Set;
import java.util.stream.Collectors;

public class Day6 {


    public static void main(String[] args) throws IOException {
        Day6 day6 = new Day6();
        day6.partOne(null);
        day6.partTwo(null);
    }

    public void partOne(String filename) throws IOException {
        List<String> inputs =
                List.of("");

        var ref = new Object() {
            int position;
            int toPosition;
        };
        inputs.forEach(input -> {
            ref.position = 0;
            ref.toPosition = 4;

            while (ref.position + ref.toPosition <= input.length()) {
                String substring = input.substring(ref.position, ref.position + ref.toPosition);
                ref.position += 1;
                if (ref.position + ref.toPosition > input.length()) {
                    ref.toPosition = Math.abs(input.length() - ref.position);
                    if (ref.toPosition == 0) {
                        break;
                    }
                }
                Set<Character> set =
                        substring.chars().mapToObj(e -> (char) e).collect(Collectors.toSet());
                if (set.size() < substring.length()) {
                } else {
                    System.out.println(
                            "Unique found: " + substring + " at: " + (ref.position + ref.toPosition - 1) + "\n");
                    break;
                }

            }
        });
    }



    public void partTwo(String filename) throws IOException {
        List<String> inputs =
                List.of("");

        var ref = new Object() {
            int position;
            int toPosition;
        };
        inputs.forEach(input -> {
            ref.position = 0;
            ref.toPosition = 14;
            while (ref.position + ref.toPosition <= input.length()) {
                String substring = input.substring(ref.position, ref.position + ref.toPosition);
                ref.position += 1;
                if (ref.position + ref.toPosition > input.length()) {
                    ref.toPosition = Math.abs(input.length() - ref.position);
                    if (ref.toPosition == 0) {
                        break;
                    }
                }
                Set<Character> set =
                        substring.chars().mapToObj(e -> (char) e).collect(Collectors.toSet());

                if (set.size() < substring.length()) {
                } else {
                    System.out.println(
                            "Unique found: " + substring + " at: " + (ref.position + ref.toPosition - 1) + "\n");
                    break;
                }

            }
        });
    }


}
